CREATE OR REPLACE PROCEDURE pls_alterar_inclusao_coop ( nr_seq_inclusao_p bigint, nr_seq_cooperado_p bigint, ie_acao_p text, nm_usuario_p text) AS $body$
DECLARE


/*	ie_acao_p
	A - Aceita
	R - Rejeitada
	V - Vincular
*/
BEGIN

if (ie_acao_p = 'A') then
	update	pls_inclusao_cooperado
	set	dt_aceitacao		= clock_timestamp(),
		nm_usuario		= nm_usuario_p,
		dt_atualizacao		= clock_timestamp()
	where	nr_sequencia		= nr_seq_inclusao_p;
elsif (ie_acao_p = 'R') then
	update	pls_inclusao_cooperado
	set	dt_rejeicao		= clock_timestamp(),
		nm_usuario		= nm_usuario_p,
		dt_atualizacao		= clock_timestamp()
	where	nr_sequencia		= nr_seq_inclusao_p;
elsif (ie_acao_p = 'V') and (nr_seq_cooperado_p IS NOT NULL AND nr_seq_cooperado_p::text <> '') then
	update	pls_inclusao_cooperado
	set	nr_seq_cooperado	= nr_seq_cooperado_p,
		nm_usuario		= nm_usuario_p,
		dt_atualizacao		= clock_timestamp()
	where	nr_sequencia		= nr_seq_inclusao_p;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
