CREATE OR REPLACE PROCEDURE pls_alterar_incl_benef ( nr_seq_contrato_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint) AS $body$
DECLARE


ie_novo_beneficiario_w	varchar(1);


BEGIN

select	coalesce(ie_novo_beneficiario,'N')
into STRICT	ie_novo_beneficiario_w
from	pls_contrato
where	nr_sequencia	= nr_seq_contrato_p;

if (ie_novo_beneficiario_w = 'N') then
	update	pls_contrato
	set	ie_novo_beneficiario	= 'S',
		nm_usuario		= nm_usuario_p
	where	nr_sequencia		= nr_seq_contrato_p;
elsif (ie_novo_beneficiario_w = 'S') then
	update	pls_contrato
	set	ie_novo_beneficiario	= 'N',
		nm_usuario		= nm_usuario_p
	where	nr_sequencia		= nr_seq_contrato_p;
end if;

insert into pls_contrato_historico(	nr_sequencia, cd_estabelecimento, nr_seq_contrato, dt_historico, ie_tipo_historico,
		dt_atualizacao, nm_usuario, dt_atualizacao_nrec, nm_usuario_nrec, ds_historico,
		ds_observacao)
	values (	nextval('pls_contrato_historico_seq'), cd_estabelecimento_p, nr_seq_contrato_p, clock_timestamp(), '67',
		clock_timestamp(), nm_usuario_p, clock_timestamp(), nm_usuario_p, 'Alterada a consistência de inclusão de beneficiários',
		'pls_alterar_incl_benef');

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
