CREATE OR REPLACE PROCEDURE pls_alterar_inconsistencia_sib ( nr_seq_inconsistencia_p bigint, ie_tipo_campo_p text, nm_usuario_p text) AS $body$
DECLARE


/*
I - IE_SITUACAO
E - IE_CONSISTIR_RESCISAO
*/
BEGIN

if (ie_tipo_campo_p = 'I') then
	update	sib_inconsistencia
	set	ie_situacao	= CASE WHEN ie_situacao='A' THEN 'I'  ELSE 'A' END ,
		nm_usuario	= nm_usuario_p,
		dt_atualizacao	= clock_timestamp()
	where	nr_sequencia	= nr_seq_inconsistencia_p;
elsif (ie_tipo_campo_p = 'E') then

	update	sib_inconsistencia
	set	IE_CONSISTIR_RESCISAO	= CASE WHEN IE_CONSISTIR_RESCISAO='S' THEN 'N'  ELSE 'S' END ,
		nm_usuario		= nm_usuario_p,
		dt_atualizacao		= clock_timestamp()
	where	nr_sequencia		= nr_seq_inconsistencia_p;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
