CREATE OR REPLACE PROCEDURE pls_alterar_lancamento_mens ( nr_seq_vinculo_sca_p text, ie_tipo_lancamento_p text, ie_opcao_rg_p text, ie_opcao_p text, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE


ie_lancamento_mensalidade_w	varchar(2);
nr_seq_segurado_w		bigint;
nr_seq_seg_historico_w		bigint;
ds_historico_w			varchar(255);
ds_observacao_w			varchar(255);
nr_seq_plano_w			bigint;
nr_seq_contrato_w			bigint;
nr_seq_vinculo_sca_w		bigint;

C01 CURSOR FOR
	SELECT	a.nr_seq_segurado,
		a.ie_lancamento_mensalidade,
		a.nr_sequencia
	from	pls_sca_vinculo a
	where	a.nr_sequencia	= nr_seq_vinculo_sca_p
	and	ie_opcao_rg_p = 'B'
	
UNION ALL

	SELECT	a.nr_seq_segurado,
	         	a.ie_lancamento_mensalidade,
  	         	a.nr_sequencia
	from  	pls_sca_vinculo a,
	          	pls_segurado     b
	where	b.nr_sequencia	= a.nr_seq_segurado
	and	a.nr_seq_plano	= nr_seq_plano_w
	and	b.nr_seq_contrato	= nr_seq_contrato_w
	and	ie_opcao_rg_p = 'S';


BEGIN

select	a.nr_seq_plano,
	b.nr_seq_contrato
into STRICT	nr_seq_plano_w,
	nr_seq_contrato_w
from	pls_sca_vinculo a,
	pls_segurado b
where	a.nr_Seq_segurado = b.nr_sequencia
and	a.nr_sequencia 	= nr_seq_vinculo_sca_p;

open C01;
loop
fetch C01 into
	nr_seq_segurado_w,
	ie_lancamento_mensalidade_w,
	nr_seq_vinculo_sca_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	if (coalesce(ie_lancamento_mensalidade_w,'0') <> '0') then
		ds_historico_w	:= 'Lançamento Anterior - ' ||  SUBSTR(obter_valor_dominio(3372,ie_lancamento_mensalidade_w),1,255);
	else
		ds_historico_w	:= 'Lançamento Anterior  ';
	end if;

	update	pls_sca_vinculo
	set	ie_lancamento_mensalidade	= ie_tipo_lancamento_p
	where	nr_sequencia			= nr_seq_vinculo_sca_w;

	select	nextval('pls_segurado_historico_seq')
	into STRICT	nr_seq_seg_historico_w
	;

	CALL pls_gerar_segurado_historico(	nr_seq_segurado_w, '38', clock_timestamp(), ds_historico_w,
					'pls_alterar_lancamento_mens', null, null, null,
					null, clock_timestamp(), null, null,
					null, null, null, null,
					nm_usuario_p, 'N');
	end;
end loop;
close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
