CREATE OR REPLACE PROCEDURE pls_alterar_lote_portal ( nr_seq_lote_p pls_lote_pagamento.nr_sequencia%type, ie_opcao_p text) AS $body$
DECLARE

/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade: Alterar o checkbox "IE_EXIBE_PORTAL" da "PLS_LOTE_PAGAMENTO"
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[ ]  Objetos do dicionário [x] Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [ ] Outros:
-------------------------------------------------------------------------------------------------------------------
Pontos de atenção:

'S' - Disponibiliza no portal
'N' - Não disponibilzia no portal
'RC' - Não disponibiliza no portal nem para recurso

Alterações:
-------------------------------------------------------------------------------------------------------------------
-------------------------------------------------------------------------------------------------------------------
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
ie_exibe_portal_w	pls_lote_pagamento.ie_exibe_portal%type := 'S';


BEGIN

if (ie_opcao_p = '0') then
	ie_exibe_portal_w := 'S';
elsif (ie_opcao_p = '1') then
	ie_exibe_portal_w := 'N';
elsif (ie_opcao_p = '2') then
	ie_exibe_portal_w := 'R';
end if;

update	pls_lote_pagamento
set	ie_exibe_portal = ie_exibe_portal_w
where	nr_sequencia = nr_seq_lote_p;
commit;

end;
$body$
