SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_alterar_matricula_estip ( nr_seq_segurado_p bigint, cd_matricula_p text, nm_usuario_p text, ie_gerar_cartao_p text, cd_estabelecimento_p bigint) AS $body$
DECLARE


dt_validade_w			timestamp;
dt_inicio_vig_cart_w		timestamp;
cd_matricula_estipulante_w	varchar(30);
nr_seq_titular_w		pls_segurado.nr_seq_titular%type;
vl_parametro_w			varchar(2);
nr_seq_contrato_w		pls_segurado.nr_seq_contrato%type;
qt_registros_w			bigint;
cd_matricula_familia_w		pls_segurado.cd_matricula_familia%type;


BEGIN

vl_parametro_w := Obter_Param_Usuario(1202, 162, obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_p, vl_parametro_w);

if (vl_parametro_w = 'S') then
	select	max(nr_seq_contrato),
		max(cd_matricula_familia)
	into STRICT	nr_seq_contrato_w,
		cd_matricula_familia_w
	from	pls_segurado
	where 	nr_sequencia = nr_seq_segurado_p;

	select 	count(*)
	into STRICT 	qt_registros_w
	from 	pls_segurado
	where 	nr_seq_contrato = nr_seq_contrato_w
	and 	cd_matricula_familia <> cd_matricula_familia_w
	and	cd_matricula_estipulante = cd_matricula_p
	and	nr_sequencia <> nr_seq_segurado_p;

	if (qt_registros_w > 0) then
		CALL wheb_mensagem_pck.exibir_mensagem_abort(1003947);
	end if;
end if;

select	cd_matricula_estipulante,
	nr_seq_titular
into STRICT	cd_matricula_estipulante_w,
	nr_seq_titular_w
from	pls_segurado
where	nr_sequencia	= nr_seq_segurado_p;

update 	pls_segurado
set 	cd_matricula_estipulante	= cd_matricula_p
where	nr_sequencia 			= nr_seq_segurado_p;

if (coalesce(ie_gerar_cartao_p,'N') = 'S')  then
	select	max(dt_validade_carteira),
		max(dt_inicio_vigencia)
	into STRICT	dt_validade_w,
		dt_inicio_vig_cart_w
	from	pls_segurado_carteira
	where	nr_seq_segurado	= nr_seq_segurado_p;

	CALL pls_gerar_carteira_usuario(nr_seq_segurado_p, nr_seq_titular_w, dt_inicio_vig_cart_w, dt_validade_w, 'P', 'ME',null, nm_usuario_p);
end if;

CALL pls_gerar_segurado_historico(
	nr_seq_segurado_p, '21', clock_timestamp(),
	'Alteração da matrícula do estipulante de ' || cd_matricula_estipulante_w || ' para ' || cd_matricula_p,
	'pls_alterar_matricula_estip',
	null,
	null, null, null,
	clock_timestamp(), null, null,
	null, null, null,
	null, nm_usuario_p, 'S');

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
