CREATE OR REPLACE PROCEDURE pls_alterar_mes_cobranca_reaj ( nr_seq_contrato_p bigint, ie_mes_cobranca_reaj_p text, nm_usuario_p text, cd_estabelecimento_p bigint) AS $body$
DECLARE


ds_mes_cobranca_antigo_w	varchar(255);
ds_mes_cobranca_novo_w		varchar(255);


BEGIN

if (coalesce(nr_seq_contrato_p,0) <> 0) then

	select	CASE WHEN ie_mes_cobranca_reaj='R' THEN  'Conforme regra' WHEN ie_mes_cobranca_reaj='M' THEN  'Mês de aniversário' WHEN ie_mes_cobranca_reaj='P' THEN  'Mês posterior ao aniversário'  ELSE null END ,
		CASE WHEN ie_mes_cobranca_reaj_p='R' THEN  'Conforme regra' WHEN ie_mes_cobranca_reaj_p='M' THEN  'Mês de aniversário' WHEN ie_mes_cobranca_reaj_p='P' THEN  'Mês posterior ao aniversário'  ELSE null END
	into STRICT	ds_mes_cobranca_antigo_w,
		ds_mes_cobranca_novo_w
	from	pls_contrato
	where	nr_sequencia = nr_seq_contrato_p;

	update	pls_contrato
	set	ie_mes_cobranca_reaj 	= ie_mes_cobranca_reaj_p,
		dt_atualizacao 		= clock_timestamp(),
		nm_usuario 		= nm_usuario_p
	where	nr_sequencia		= nr_seq_contrato_p;

	insert into pls_contrato_historico(	nr_sequencia, cd_estabelecimento, nr_seq_contrato, dt_historico, ie_tipo_historico,
			dt_atualizacao, nm_usuario, dt_atualizacao_nrec, nm_usuario_nrec,
			ds_historico,ds_observacao)
		values (	nextval('pls_contrato_historico_seq'), cd_estabelecimento_p, nr_seq_contrato_p, clock_timestamp(), '63',
			clock_timestamp(), nm_usuario_p, clock_timestamp(), nm_usuario_p,
			'De: ' || ds_mes_cobranca_antigo_w || '. Para: ' || ds_mes_cobranca_novo_w,
			'pls_alterar_mes_cobranca_reaj');

end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
