CREATE OR REPLACE PROCEDURE pls_alterar_nascido_plano ( nr_seq_segurado_p bigint, nm_usuario_p text) AS $body$
DECLARE


ie_nascido_plano_w	varchar(10);
ds_alteracao_nascido_w	varchar(100);


BEGIN

select	coalesce(ie_nascido_plano,'N')
into STRICT	ie_nascido_plano_w
from	pls_segurado
where	nr_sequencia		= nr_seq_segurado_p;

if (ie_nascido_plano_w	= 'N') then
	ds_alteracao_nascido_w	:= 'Alteração do beneficiário de "não nascido pelo plano" para "nascido pelo plano"';
elsif (ie_nascido_plano_w	= 'S') then
	ds_alteracao_nascido_w	:= 'Alteração do beneficiário de "nascido pelo plano" para "não nascido pelo plano"';
end if;

update	pls_segurado
set	ie_nascido_plano	= CASE WHEN ie_nascido_plano_w='S' THEN 'N' WHEN ie_nascido_plano_w='N' THEN 'S' END ,
	nm_usuario		= nm_usuario_p,
	dt_atualizacao		= clock_timestamp()
where	nr_sequencia		= nr_seq_segurado_p;

CALL pls_gerar_segurado_historico(	nr_seq_segurado_p, '35', clock_timestamp(), ds_alteracao_nascido_w,
				'pls_alterar_nascido_plano', null, null, null,
				null, null, null, null,
				null, null, null, null,
				nm_usuario_p, 'N');

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
