CREATE OR REPLACE PROCEDURE pls_alterar_observacao_fatura (nr_seq_fatura_p bigint, ds_observacao_p text, nm_usuario_p text) AS $body$
BEGIN
if (nr_seq_fatura_p IS NOT NULL AND nr_seq_fatura_p::text <> '') then
	update	ptu_fatura
	set	ds_observacao_inf 	= ds_observacao_p,
		dt_atualizacao		= clock_timestamp(),
		nm_usuario		= nm_usuario_p
	where	nr_sequencia		= nr_seq_fatura_p;

	commit;
end if;

end;
