SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_alterar_pagador_principal ( nr_seq_pagador_p bigint, nr_seq_contrato_p bigint, nm_usuario_p text, cd_estabelecimento_p text) AS $body$
DECLARE


nr_seq_pagador_anterior_w 		pls_contrato_pagador.nr_sequencia%type;


BEGIN

--Armazena o nr_sequencia do pagador principal
select 	max(nr_sequencia)
into STRICT	nr_seq_pagador_anterior_w
from	pls_contrato_pagador
where	nr_seq_contrato	= nr_seq_contrato_p
and	ie_tipo_pagador = 'P';

--Altera o pagador principal para secundário
update	pls_contrato_pagador
set	ie_tipo_pagador	= 'S'
where	nr_sequencia	= nr_seq_pagador_anterior_w;

--Altera o pagador secundário para principal
update	pls_contrato_pagador
set	ie_tipo_pagador	= 'P'
where	nr_sequencia	= nr_seq_pagador_p;

--Insere histórico de alteração de cadastro do pagador secundário para primário
insert	into	pls_pagador_historico(	nr_sequencia,
		dt_historico,
		nm_usuario_historico,
		ds_titulo,
		ds_historico,
		nr_seq_pagador,
		cd_estabelecimento,
		dt_atualizacao,
		nm_usuario,
		dt_atualizacao_nrec,
		nm_usuario_nrec,
		ie_tipo_historico)
	values (	nextval('pls_pagador_historico_seq'),
		clock_timestamp(),
		nm_usuario_p,
		'Alteração no cadastro do pagador',
		'Alterado o pagador, de "Secundário" para "Principal".',
		nr_seq_pagador_p,
		cd_estabelecimento_p,
		clock_timestamp(),
		nm_usuario_p,
		clock_timestamp(),
		nm_usuario_p,
		'S');

if (nr_seq_pagador_anterior_w IS NOT NULL AND nr_seq_pagador_anterior_w::text <> '') then
	--Insere histórico de alteração de cadastro do pagador primário para secundário
	insert	into	pls_pagador_historico(	nr_sequencia,
			dt_historico,
			nm_usuario_historico,
			ds_titulo,
			ds_historico,
			nr_seq_pagador,
			cd_estabelecimento,
			dt_atualizacao,
			nm_usuario,
			dt_atualizacao_nrec,
			nm_usuario_nrec,
			ie_tipo_historico)
		values (	nextval('pls_pagador_historico_seq'),
			clock_timestamp(),
			nm_usuario_p,
			'Alteração no cadastro do pagador',
			'Alterado o pagador, de "Principal" para "Secundário".',
			nr_seq_pagador_anterior_w,
			cd_estabelecimento_p,
			clock_timestamp(),
			nm_usuario_p,
			clock_timestamp(),
			nm_usuario_p,
			'S');
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
