CREATE OR REPLACE PROCEDURE pls_alterar_pagador_segurado ( nr_seq_segurado_p bigint, nr_seq_pagador_antigo_p bigint, nr_seq_pagador_novo_p bigint, dt_geracao_sib_p timestamp, ie_commit_p text, nm_usuario_p text) AS $body$
DECLARE


nr_seq_seg_pagador_w		pls_segurado_pagador.nr_sequencia%type;

BEGIN
/*
	Procedure atualiza o pagador vigente do beneficiário toda vez que o mesmo é alterado
*/
select	max(nr_sequencia)
into STRICT	nr_seq_seg_pagador_w
from	pls_segurado_pagador
where	nr_seq_segurado = nr_seq_segurado_p
and	coalesce(dt_fim_vigencia::text, '') = '';

if (nr_seq_seg_pagador_w IS NOT NULL AND nr_seq_seg_pagador_w::text <> '') then
	update	pls_segurado_pagador
	set	dt_fim_vigencia = fim_dia(trunc(dt_geracao_sib_p,'mm') - 1)
	where	nr_sequencia = nr_seq_seg_pagador_w;

	insert into pls_segurado_pagador(nr_sequencia, dt_atualizacao, nm_usuario,
		nr_seq_segurado, nr_seq_pagador, dt_inicio_vigencia
		)
	values (nextval('pls_segurado_pagador_seq'), clock_timestamp(), nm_usuario_p,
		nr_seq_segurado_p, nr_seq_pagador_novo_p, trunc(dt_geracao_sib_p,'mm')
		);

	if (coalesce(ie_commit_p,'N') = 'S') then
		commit;
	end if;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
