CREATE OR REPLACE PROCEDURE pls_alterar_pagador_seg_interc ( nr_seq_segurado_p bigint, nr_seq_pagador_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint) AS $body$
DECLARE


nr_seq_segurado_w		pls_segurado.nr_sequencia%type;
nr_seq_pagador_w		pls_contrato_pagador.nr_sequencia%type;
nm_pagador_w			varchar(255);

c01 CURSOR FOR
	SELECT	nr_sequencia,
		nr_seq_pagador
	from	pls_segurado
	where	((nr_sequencia	= nr_seq_segurado_p)
	or (nr_seq_titular	= nr_seq_segurado_p))
	and	((coalesce(dt_rescisao::text, '') = '') or (dt_rescisao >= clock_timestamp()))
	and	(dt_liberacao IS NOT NULL AND dt_liberacao::text <> '')
	order by
		nr_seq_titular desc;


BEGIN

if (coalesce(nr_seq_segurado_p,0) <> 0) then
	open c01;
	loop
	fetch c01 into
		nr_seq_segurado_w,
		nr_seq_pagador_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
		begin
		nm_pagador_w	:= substr(pls_obter_dados_pagador(nr_seq_pagador_w,'N'),1,255);

		update	pls_segurado
		set	nr_seq_pagador	= nr_seq_pagador_p,
			dt_atualizacao	= clock_timestamp(),
			nm_usuario	= nm_usuario_p
		where	nr_sequencia	= nr_seq_segurado_w;

		CALL pls_alterar_pagador_segurado(nr_seq_segurado_w,nr_seq_pagador_w,nr_seq_pagador_p,clock_timestamp(),'N',nm_usuario_p);

		/* Gerar hist√≥rico */

		CALL pls_gerar_segurado_historico(
			nr_seq_segurado_w, '6', clock_timestamp(),
			wheb_mensagem_pck.get_texto(280132, 'NR_SEQ_PAGADOR_P=' || to_char(nr_seq_pagador_w) || ';NM_PAGAD0R_P=' || nm_pagador_w),'pls_alterar_pagador_seg_interc', null,
			null, null, null,
			null, null, null,
			null, null, null,
			null, nm_usuario_p, 'S');
		end;
	end loop;
	close c01;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
