CREATE OR REPLACE PROCEDURE pls_alterar_periodo_renovacao ( nr_sequencia_p bigint, qt_dia_periodo_p text, nm_usuario_p text, cd_estabelecimento_p bigint) AS $body$
BEGIN

update	pls_contrato
set	qt_intervalo	= qt_dia_periodo_p,
	nm_usuario	= nm_usuario_p,
	dt_atualizacao	= clock_timestamp()
where	nr_sequencia	= nr_sequencia_p;

insert into pls_contrato_historico(	nr_sequencia, cd_estabelecimento, nr_seq_contrato, dt_historico, ie_tipo_historico,
		dt_atualizacao, nm_usuario, dt_atualizacao_nrec, nm_usuario_nrec, ds_historico,
		ds_observacao)
	values (	nextval('pls_contrato_historico_seq'), cd_estabelecimento_p, nr_sequencia_p, clock_timestamp(), '67',
		clock_timestamp(), nm_usuario_p, clock_timestamp(), nm_usuario_p, 'Alterado o período de renovação do contrato',
		'pls_alterar_periodo_renovacao');

commit;

end;
$body$
