CREATE OR REPLACE PROCEDURE pls_alterar_pessoa_benef_event ( nr_seq_segurado_p bigint, cd_pessoa_fisica_p text, nm_usuario_p text) AS $body$
DECLARE


nr_seq_segurado_w	bigint;
cd_pessoa_fisica_ant_w	pessoa_fisica.cd_pessoa_fisica%type;
nm_pessoa_fisica_ant_w	varchar(255);


BEGIN

if (coalesce(cd_pessoa_fisica_p,'0') <> '0') then
	begin
	select 	cd_pessoa_fisica,
		obter_nome_pf(cd_pessoa_fisica)
	into STRICT	cd_pessoa_fisica_ant_w,
		nm_pessoa_fisica_ant_w
	from 	pls_segurado
	where 	nr_sequencia 	= nr_seq_segurado_p;
	exception
	when others then
		cd_pessoa_fisica_ant_w	:= null;
		nm_pessoa_fisica_ant_w	:= null;
	end;
	
	if (coalesce(cd_pessoa_fisica_ant_w,0) <> cd_pessoa_fisica_p) then
		update	pls_segurado
		set	cd_pessoa_fisica	= cd_pessoa_fisica_p,
			dt_atualizacao		= clock_timestamp(),
			nm_usuario		= nm_usuario_p
		where	nr_sequencia		= nr_seq_segurado_p;
		
		CALL pls_gerar_segurado_historico(
			nr_seq_segurado_p, '105', clock_timestamp(),
			wheb_mensagem_pck.get_texto(321828, 'CD_PESSOA_FISICA_P=' || to_char(cd_pessoa_fisica_ant_w)||' - '||nm_pessoa_fisica_ant_w),'pls_alterar_pessoa_benef_event', null,
			null, null, null,
			clock_timestamp(), null, null,
			null, null, null,
			null, nm_usuario_p, 'S');
	end if;
end if;

commit;

end;
$body$
