CREATE OR REPLACE PROCEDURE pls_alterar_plano_benef_prop ( nr_seq_beneficiario_p bigint, nr_seq_plano_p bigint, nr_seq_tabela_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_proposta_w	bigint;
qt_plano_proposta_w	bigint;


BEGIN

select	nr_seq_proposta
into STRICT	nr_seq_proposta_w
from	pls_proposta_beneficiario
where	nr_sequencia	= nr_seq_beneficiario_p;

select	count(*)
into STRICT	qt_plano_proposta_w
from	pls_proposta_plano
where	nr_seq_proposta	= nr_seq_proposta_w
and	nr_seq_plano	= nr_seq_plano_p
and	nr_seq_tabela	= nr_seq_tabela_p;

if (qt_plano_proposta_w > 0) then
	update	pls_proposta_beneficiario
	set	nr_seq_plano	= nr_seq_plano_p,
		nr_seq_tabela	= nr_seq_tabela_p
	where	nr_sequencia	= nr_seq_beneficiario_p;

	CALL pls_lanc_bonificacao_proposta(nr_seq_beneficiario_p, nr_seq_proposta_w,	nm_usuario_p);
elsif (qt_plano_proposta_w = 0) then
	CALL wheb_mensagem_pck.exibir_mensagem_abort(265193,'');
	--Menssagem: O produto não está vinculado a proposta de adesão!.
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
