SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_alterar_produto_simulacao ( nr_seq_simulacao_p bigint, nr_seq_produto_p bigint) AS $body$
DECLARE


qt_simulacao_precos_w	bigint;
nr_seq_produto_w		bigint;
nr_seq_tabela_w			bigint;


BEGIN

select	count(*)
into STRICT	qt_simulacao_precos_w
from	pls_simulpreco_individual
where	nr_seq_simulacao	= nr_seq_simulacao_p;

nr_seq_produto_w	:= coalesce(nr_seq_produto_p,0);


if (qt_simulacao_precos_w > 0) then
	if (nr_seq_produto_w = 0) then
		update	pls_simulpreco_individual
		set		nr_seq_produto		 = NULL,
				nr_seq_tabela		 = NULL
		where	nr_seq_simulacao	= nr_seq_simulacao_p;
	else

		select	max(nr_seq_tabela)
		into STRICT	nr_seq_tabela_w
		from	pls_regra_simulador_web
		where	nr_seq_plano	= nr_seq_produto_w
		and		clock_timestamp() between dt_inicio_vigencia and coalesce(dt_fim_vigencia,clock_timestamp());

		update	pls_simulpreco_individual
		set		nr_seq_produto		= nr_seq_produto_w,
				nr_seq_tabela		= coalesce(nr_seq_tabela_w,null)
		where	nr_seq_simulacao	= nr_seq_simulacao_p;
	end if;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
