CREATE OR REPLACE PROCEDURE pls_alterar_profissional_exec ( cd_medico_executor_p bigint, nr_sequencia_p bigint, nm_usuario_p text) AS $body$
DECLARE

 
nr_crm_exec_w		varchar(20);
nr_seq_cbo_saude_w	bigint;
uf_crm_exec_w		varchar(2);
nr_seq_conselho_exec_w	bigint;


BEGIN 
if (cd_medico_executor_p IS NOT NULL AND cd_medico_executor_p::text <> '') and (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') and (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') then 
	begin 
	 
	select 	substr(obter_crm_medico(cd_medico_executor_p),1,255), 
		substr(obter_dados_medico(cd_medico_executor_p, 'UFCRM'),1,255), 
		substr(pls_obter_seq_conselho_prof(cd_medico_executor_p),1,255) 
	into STRICT	nr_crm_exec_w, 
		uf_crm_exec_w, 
		nr_seq_conselho_exec_w 
	;
	 
	select max(nr_seq_cbo_saude) 
	into STRICT	nr_seq_cbo_saude_w 
    from  medico_especialidade 
    where  cd_pessoa_fisica = cd_medico_executor_p 
    and   nr_seq_prioridade = (	SELECT  min(nr_seq_prioridade) 
					from   medico_especialidade 
                    where  cd_pessoa_fisica = cd_medico_executor_p ) 
    and  (nr_seq_cbo_saude IS NOT NULL AND nr_seq_cbo_saude::text <> '');
																   
	update	pls_conta 
	set	cd_medico_executor 	= cd_medico_executor_p, 
		nr_crm_exec 		= nr_crm_exec_w, 
		nr_seq_cbo_saude	= nr_seq_cbo_saude_w, 
		uf_crm_exec		= uf_crm_exec_w, 
		nr_seq_conselho_exec	= nr_seq_conselho_exec_w, 
		nm_usuario 		= nm_usuario_p 
	where 	nr_sequencia 		= nr_sequencia_p;
	end;
 
end if;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
