CREATE OR REPLACE PROCEDURE pls_alterar_qt_autorizada ( nr_sequencia_p bigint, qt_autorizada_p bigint, ie_opcao_p text, nm_usuario_p text) AS $body$
DECLARE


/* ie_opcao_p:
M = materiais
P  = procedimentos
*/
BEGIN

if (qt_autorizada_p IS NOT NULL AND qt_autorizada_p::text <> '') and (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') then
	begin

	if (ie_opcao_p = 'P') then
		update	pls_guia_plano_proc
		set	qt_autorizada	= qt_autorizada_p,
			dt_atualizacao	= clock_timestamp(),
			nm_usuario	= nm_usuario_p
		where	nr_sequencia	= nr_sequencia_p;
	elsif (ie_opcao_p = 'M') then
		update	pls_guia_plano_mat
		set	qt_autorizada	= qt_autorizada_p,
			dt_atualizacao	= clock_timestamp(),
			nm_usuario	= nm_usuario_p
		where	nr_sequencia	= nr_sequencia_p;
	end if;

	commit;

	end;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
