CREATE OR REPLACE PROCEDURE pls_alterar_quant_analise_pos (nr_seq_item_p bigint, qt_item_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE


qt_apresentado_w	double precision;
qt_item_w		double precision;
nr_seq_conta_w		bigint;
nr_seq_analise_w	bigint;
ie_tipo_item_w		varchar(1);
ds_observacao_w		varchar(4000);
ds_item_w		varchar(250);
cd_item_w		bigint;
ds_item_analise_w	varchar(250);


BEGIN
select	max(qt_apresentado)
into STRICT	qt_apresentado_w
from	w_pls_resumo_conta
where	nr_sequencia = nr_seq_item_p;

if (qt_apresentado_w <> qt_item_p) then
	select	nr_seq_conta,
		nr_seq_analise,
		ie_tipo_item,
		qt_apresentado,
		lower(substr(pls_obter_desc_item_desp(ds_tipo_despesa, 'S'),1,250)),
		cd_item,
		substr(ds_item,1,250)
	into STRICT	nr_seq_conta_w,
		nr_seq_analise_w,
		ie_tipo_item_w,
		qt_item_w,
		ds_item_w,
		cd_item_w,
		ds_item_analise_w
	from	w_pls_resumo_conta
	where	nr_sequencia	= nr_seq_item_p;

	update	w_pls_resumo_conta
	set	qt_apresentado	= qt_item_p
	where	nr_sequencia	= nr_seq_item_p;

	ds_item_w := upper(substr(ds_item_w,1,1)) || substr(ds_item_w,2,length(ds_item_w));

	ds_observacao_w :=	ds_item_w || ' alterado: ' || cd_item_w || ' - ' || ds_item_analise_w || chr(13) || chr(10) || chr(13) || chr(10) ||
				'Quantidade anterior: ' || qt_item_w || chr(13) || chr(10) ||
				'Quantidade atual: ' || qt_item_p;

	-- Inserir hist√≥rico
	CALL pls_inserir_hist_analise(nr_seq_conta_w,nr_seq_analise_w,32,nr_seq_item_p,ie_tipo_item_w,null,null,
				ds_observacao_w,null,nm_usuario_p,cd_estabelecimento_p);

commit;

end if;

end;
$body$
