CREATE OR REPLACE PROCEDURE pls_alterar_responsavel_equipe ( nr_seq_equipe_p bigint, cd_pessoa_responsavel_p text, cd_estabelecimento_p bigint, nm_usuario_p text, qt_grupos_p INOUT bigint) AS $body$
DECLARE


nr_seq_grupo_contrato_w		pls_grupo_contrato_equipe.nr_seq_grupo_contrato%type;
qt_grupos_w			bigint;

C01 CURSOR FOR
	SELECT	a.nr_seq_grupo_contrato
	from	pls_grupo_contrato_equipe 	a
	where 	a.nr_seq_equipe 	= nr_seq_equipe_p
	and	coalesce(a.dt_fim_vigencia::text, '') = ''
	group by
		a.nr_seq_grupo_contrato;

BEGIN

select	count(1)
into STRICT	qt_grupos_w
from	pls_grupo_contrato_equipe 	a
where 	a.nr_seq_equipe 	= nr_seq_equipe_p
and	coalesce(a.dt_fim_vigencia::text, '') = ''  LIMIT 1;

qt_grupos_p	:= qt_grupos_w;

open C01;
loop
fetch C01 into
	nr_seq_grupo_contrato_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	update 	pls_grupo_contrato_equipe
	set	dt_fim_vigencia		= clock_timestamp()
	where	nr_seq_grupo_contrato	= nr_seq_grupo_contrato_w
	and	coalesce(dt_fim_vigencia::text, '') = '';

	insert into pls_grupo_contrato_equipe(	nr_sequencia,
			nr_seq_grupo_contrato,
			cd_estabelecimento,
			nr_seq_equipe,
			dt_atualizacao,
			nm_usuario,
			dt_atualizacao_nrec,
			nm_usuario_nrec,
			dt_inicio_vigencia,
			dt_fim_vigencia,
			cd_pessoa_responsavel	)
	values (	nextval('pls_grupo_contrato_equipe_seq'),
			nr_seq_grupo_contrato_w,
			cd_estabelecimento_p,
			nr_seq_equipe_p,
			clock_timestamp(),
			nm_usuario_p,
			clock_timestamp(),
			nm_usuario_p,
			clock_timestamp(),
			null,
			cd_pessoa_responsavel_p	);
	end;

end loop;
close C01;

commit;

end;
$body$
