CREATE OR REPLACE PROCEDURE pls_alterar_responsavel_sede ( nr_seq_proposta_p bigint, nm_usuario_p text) AS $body$
DECLARE


ie_presente_sede_w	varchar(10);


BEGIN

select	coalesce(ie_presente_sede,'N')
into STRICT	ie_presente_sede_w
from	pls_proposta_adesao
where	nr_sequencia	= nr_seq_proposta_p;

update	pls_proposta_adesao
set	ie_presente_sede	= CASE WHEN ie_presente_sede_w='N' THEN 'S'  ELSE 'N' END ,
	nm_usuario		= nm_usuario_p
where	nr_sequencia		= nr_seq_proposta_p;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
