CREATE OR REPLACE PROCEDURE pls_alterar_senha_proponente ( ds_senha_p pls_solic_credenciame_user.ds_senha%type, ds_tec_p pls_solic_credenciame_user.ds_tec%type, nm_usuario_p pls_solic_credenciame_user.nm_usuario%type, ie_ativar_usuario_p pls_solic_credenciame_user.ie_situacao%type ) AS $body$
BEGIN

if ie_ativar_usuario_p = 'S' then
	update pls_solic_credenciame_user
	set ds_senha = ds_senha_p,
	    ie_situacao = 'A',
	    dt_inativacao  = NULL,
		ds_tec = ds_tec_p
	where nm_usuario = nm_usuario_p;
else
	update pls_solic_credenciame_user
	set ds_senha = ds_senha_p,
		ds_tec = ds_tec_p
	where nm_usuario = nm_usuario_p;
end if;

end;
$body$
