CREATE OR REPLACE PROCEDURE pls_alterar_situacao_cartao ( nr_sequencia_p bigint, nm_usuario_p text) AS $body$
DECLARE


ie_situacao_w		varchar(1)	:= 'D';

/*Utilizada nas funções OPS - Gestão de Contratos e OPS - Contratos de Intercâmbio*/

BEGIN

select	ie_situacao
into STRICT	ie_situacao_w
from	pls_segurado_carteira
where	nr_sequencia	= nr_sequencia_p;

if (ie_situacao_w	= 'D') then
	ie_situacao_w 	:= 'P';
elsif (ie_situacao_w	= 'P') then
	ie_situacao_w 	:= 'D';
end if;

update	pls_segurado_carteira
set	ie_situacao	= ie_situacao_w,
	nm_usuario	= nm_usuario_p,
	dt_atualizacao	= clock_timestamp()
where  	nr_sequencia	= nr_sequencia_p;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
