CREATE OR REPLACE PROCEDURE pls_alterar_situacao_rol ( nr_sequencia_p bigint, ie_acao_p text, nm_usuario_p text) AS $body$
BEGIN

if (ie_acao_p = 'C') then
	update	pls_rol_capitulo
	set	ie_situacao	= CASE WHEN coalesce(ie_situacao,'A')='A' THEN 'I'  ELSE 'A' END
	where	nr_sequencia	= nr_sequencia_p;
elsif (ie_acao_p = 'G') then
	update	pls_rol_grupo
	set	ie_situacao	= CASE WHEN coalesce(ie_situacao,'A')='A' THEN 'I'  ELSE 'A' END
	where	nr_sequencia	= nr_sequencia_p;
elsif (ie_acao_p = 'S') then
	update	pls_rol_subgrupo
	set	ie_situacao	= CASE WHEN coalesce(ie_situacao,'A')='A' THEN 'I'  ELSE 'A' END
	where	nr_sequencia	= nr_sequencia_p;
elsif (ie_acao_p = 'P') then
	update	pls_rol_grupo_proc
	set	ie_situacao	= CASE WHEN coalesce(ie_situacao,'A')='A' THEN 'I'  ELSE 'A' END
	where	nr_sequencia	= nr_sequencia_p;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
