CREATE OR REPLACE PROCEDURE pls_alterar_status_analise_cta ( nr_seq_analise_p pls_analise_conta.nr_sequencia%type, ie_status_p text, ds_rotina_p text, nm_usuario_p usuario.nm_usuario%type, cd_estabelecimento_p estabelecimento.cd_estabelecimento%type) AS $body$
DECLARE


ie_analise_cm_nova_w	pls_parametros.ie_analise_cm_nova%type;


BEGIN

update	pls_analise_conta
set	ie_status	= ie_status_p,
	nm_usuario	= nm_usuario_p,
	dt_atualizacao	= clock_timestamp(),
	nm_proc_status	= substr(ds_rotina_p,1,30)
where	nr_sequencia 	= nr_seq_analise_p;

if (ie_status_p = 'L') then

	select	coalesce(max(ie_analise_cm_nova),'N')
	into STRICT	ie_analise_cm_nova_w
	from	pls_parametros a
	where	a.cd_estabelecimento = cd_estabelecimento_p;

	if (ie_analise_cm_nova_w = 'S') then

		update	pls_auditoria_conta_grupo
		set	dt_liberacao 		= clock_timestamp(),
			dt_final_analise	= clock_timestamp(),
			nm_auditor_atual	= CASE WHEN nm_auditor_atual = NULL THEN  'Tasy'  ELSE nm_auditor_atual END ,
			dt_atualizacao		= clock_timestamp(),
			nm_usuario		= nm_usuario_p
		where	nr_seq_analise		= nr_seq_analise_p
		and	coalesce(dt_liberacao::text, '') = '';
	end if;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
