CREATE OR REPLACE PROCEDURE pls_alterar_status_analise ( nr_seq_analise_p bigint, ie_status_p text, nm_usuario_p text) AS $body$
DECLARE


ie_status_w			varchar(1);


BEGIN

select	ie_status
into STRICT	ie_status_w
from	pls_analise_adesao
where	nr_sequencia	= nr_seq_analise_p;

if (coalesce(ie_status_p,'0') = 'P') then
	if (ie_status_w	not in ('E','C')) then
		update	pls_analise_adesao
		set	ie_status	= ie_status_p
		where	nr_sequencia	= nr_seq_analise_p;
	end if;
elsif (coalesce(ie_status_p,'0') = 'B') then
	if (ie_status_w	= 'P') then
		update	pls_analise_adesao
		set	ie_status	= ie_status_p
		where	nr_sequencia	= nr_seq_analise_p;
	end if;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
