CREATE OR REPLACE PROCEDURE pls_alterar_status_atend ( nr_seq_atendimento_p bigint, ie_estagio_p text, nr_seq_tipo_historico_p bigint, ds_historico_p text, nm_usuario_p text) AS $body$
