CREATE OR REPLACE PROCEDURE pls_alterar_status_declaracao ( nr_seq_declaracao_p pls_declaracao_segurado.nr_sequencia%type, ie_opcao_p text, nm_usuario_p usuario.nm_usuario%type) AS $body$
DECLARE


/*	ie_opcao_p
	L - Liberar declaracao
	U - Usuario (aguardando acao)
	E - Enviada
	P - Preenchida
*/
ie_status_w		pls_declaracao_segurado.ie_status%type;
qt_idade_w		bigint;
qt_idade_responsavel_w	bigint;
cd_estabelecimento_w	estabelecimento.cd_estabelecimento%type;
qt_idade_min_decl_saude_w pls_parametros.qt_idade_min_decl_saude%type;


BEGIN

select	ie_status,
	obter_dados_pf(cd_pessoa_responsavel, 'I'),
	obter_dados_pf(cd_pessoa_fisica, 'I'),
	cd_estabelecimento
into STRICT	ie_status_w,
	qt_idade_responsavel_w,
	qt_idade_w,
	cd_estabelecimento_w
from	pls_declaracao_segurado
where	nr_sequencia	= nr_seq_declaracao_p;

select	max(qt_idade_min_decl_saude)
into STRICT	qt_idade_min_decl_saude_w
from	pls_parametros
where	cd_estabelecimento = cd_estabelecimento_w;

if (qt_idade_min_decl_saude_w IS NOT NULL AND qt_idade_min_decl_saude_w::text <> '') then
	if (qt_idade_w < qt_idade_min_decl_saude_w) then
		if (coalesce(qt_idade_responsavel_w::text, '') = '') then
			CALL wheb_mensagem_pck.exibir_mensagem_abort(1171963,'DS_ANO_REGRA='||qt_idade_min_decl_saude_w);
		elsif (qt_idade_responsavel_w < qt_idade_min_decl_saude_w) then
			CALL wheb_mensagem_pck.exibir_mensagem_abort(1171971,'DS_ANO_REGRA='||qt_idade_min_decl_saude_w);
		end if;
	end if;
end if;

if	not((ie_opcao_p	= 'E') and (ie_status_w	in ('P','L'))) then
	update	pls_declaracao_segurado
	set	ie_status	= ie_opcao_p,
		dt_liberacao	= CASE WHEN ie_opcao_p='L' THEN  clock_timestamp()  ELSE dt_liberacao END ,
		dt_atualizacao	= clock_timestamp(),
		nm_usuario	= nm_usuario_p
	where	nr_sequencia	= nr_seq_declaracao_p;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
