SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_alterar_status_email ( ie_status_p text, nr_seq_email_p bigint, ds_log_p text, nm_usuario_p text) AS $body$
DECLARE


				     
/*
Utilizado na funcao OPS - Gestao de Envio de Emails

ie_status_p 
'E' = Enviado
'ER' = Com Erro
'C'= Cancelado
'P'= Pendente
*/
ie_status_w		pls_email.ie_status%type;
qt_tentativa_w		pls_email.qt_tentativa%type;
qt_tentativa_param_w	pls_email_param.qt_tentativa%type;
cd_estabelecimento_w	pls_email.cd_estabelecimento%type;
		

BEGIN
--Enviado
if (ie_status_p = 'E') then

	update	pls_email
	set	ie_status = ie_status_p,
		dt_atualizacao = clock_timestamp(),
		nm_usuario = nm_usuario_p 		
	where	nr_sequencia = nr_seq_email_p;			
--Com erro			 
elsif (ie_status_p = 'ER') then
	select	qt_tentativa,
		cd_estabelecimento
	into STRICT	qt_tentativa_w,
		cd_estabelecimento_w
	from	pls_email
	where	nr_sequencia = nr_seq_email_p;
	
	select	max(qt_tentativa)
	into STRICT	qt_tentativa_param_w
	from	pls_email_param
	where	cd_estabelecimento = cd_estabelecimento_w;
	
	if (coalesce(qt_tentativa_w::text, '') = '') then
		qt_tentativa_w := 0;
	end if;
	
	if ((qt_tentativa_param_w IS NOT NULL AND qt_tentativa_param_w::text <> '') and qt_tentativa_w + 1 < qt_tentativa_param_w) then
		update	pls_email
		set	ie_status = 'P',
			dt_atualizacao = clock_timestamp(),
			nm_usuario = nm_usuario_p 		
		where	nr_sequencia = nr_seq_email_p;
	else	
		update	pls_email
		set	ie_status = ie_status_p,
			dt_atualizacao = clock_timestamp(),
			nm_usuario = nm_usuario_p 		
		where	nr_sequencia = nr_seq_email_p;
	end if;
	
	insert	into  pls_email_log( nr_sequencia, dt_atualizacao,nm_usuario,
				dt_atualizacao_nrec,nm_usuario_nrec,nr_seq_email,ds_log)
	values (nextval('pls_email_log_seq'),clock_timestamp(),nm_usuario_p,
				clock_timestamp(),nm_usuario_p,nr_seq_email_p,ds_log_p);	
--Cancelado
elsif (ie_status_p = 'C') then
	--Valida o status se o e-mail pode ser cancelado mesmo
	select	max(ie_status)
	into STRICT	ie_status_w
	from	pls_email
	where	nr_sequencia = nr_seq_email_p;
	--Com erro, Pendente ou Em Processamento
	if (ie_status_w = 'ER' or ie_status_w = 'P' or ie_status_w = 'EP') then
		update	pls_email
		set	ie_status = ie_status_p,
			dt_atualizacao = clock_timestamp(),
			nm_usuario = nm_usuario_p 		
		where	nr_sequencia = nr_seq_email_p;	
	end if;
--Pendente
elsif (ie_status_p = 'P') then
	--Valida o status se o e-mail pode ser pendente mesmo
	select	max(ie_status)
	into STRICT	ie_status_w
	from	pls_email
	where	nr_sequencia = nr_seq_email_p;
	-- Com erro ou Cancelado
	if (ie_status_w = 'ER' or ie_status_w = 'C') then		
		update	pls_email
		set	ie_status = ie_status_p,
			nr_seq_processamento  = NULL,
			dt_atualizacao = clock_timestamp(),
			nm_usuario = nm_usuario_p
		where	nr_sequencia = nr_seq_email_p;
	end if;	
end if;				

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
