SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_alterar_status_repasse ( nr_seq_repasse_p bigint, nm_usuario_p text) AS $body$
DECLARE


ie_status_w			varchar(01);
ie_novo_status_w	varchar(01);
ie_titulo_w			smallint;


BEGIN

select	ie_status
into STRICT	ie_status_w
from	pls_repasse_vend
where	nr_sequencia	= nr_seq_repasse_p;

if (ie_status_w		= 'A') then
	ie_novo_status_w	:= 'F';
else
	select	count(*)
	into STRICT	ie_titulo_w
	from	pls_repasse_vend_venc a,
			titulo_pagar b
	where	a.nr_titulo	= b.nr_titulo
	and		nr_seq_repasse = nr_seq_repasse_p;

	if (ie_titulo_w > 0) then
		/* Existe título gerado para este repasse.
		Repasse não pode ser aberto ! */
		CALL wheb_mensagem_pck.exibir_mensagem_abort(267147);
	end if;
	ie_novo_status_w	:= 'A';

	delete	from pls_repasse_vend_venc
	where	nr_seq_repasse = nr_seq_repasse_p;
end if;

update	pls_repasse_vend
set	ie_status		= ie_novo_status_w,
	nm_usuario		= nm_usuario_p,
	dt_atualizacao	= clock_timestamp()
where	nr_sequencia	= nr_seq_repasse_p;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
