CREATE OR REPLACE PROCEDURE pls_alterar_status_termo_benef ( nr_seq_segurado_termo_p pls_segurado_termo.nr_sequencia%type, ie_acao_p text, nm_usuario_p usuario.nm_usuario%type) AS $body$
DECLARE


/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade: Confirmar ou revogar o termo contratual do beneficiario.
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta: 
[  ]  Objetos do dicionario [   ] Tasy (Delphi/Java) [ X ] Portal [  ]  Relatorios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atencao:
	ie_acao_p:
		C - Confirmar Termo
		R - Revogar Termo
	PLS_SEGURADO_TERMO
-------------------------------------------------------------------------------------------------------------------
Referencias:
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
BEGIN

if (ie_acao_p = 'C') then
	update	pls_segurado_termo
	set	dt_confirmacao = clock_timestamp(),
		nm_usuario_confirmacao = nm_usuario_p
	where	nr_sequencia = nr_seq_segurado_termo_p;
elsif (ie_acao_p = 'R') then
	update	pls_segurado_termo
	set	dt_revogacao = clock_timestamp(),
		nm_usuario_revogacao = nm_usuario_p,
		dt_confirmacao = CASE WHEN dt_confirmacao = NULL THEN clock_timestamp()  ELSE dt_confirmacao END ,
		nm_usuario_confirmacao = CASE WHEN nm_usuario_confirmacao = NULL THEN nm_usuario_p  ELSE nm_usuario_confirmacao END
	where	nr_sequencia = nr_seq_segurado_termo_p;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
