CREATE OR REPLACE PROCEDURE pls_alterar_tabela_interc ( nr_seq_intercambio_p bigint, nr_seq_interc_plano_p bigint, nr_seq_plano_p bigint, nr_seq_tabela_nova_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint) AS $body$
DECLARE

 
ie_tabela_base_w		varchar(1);
nr_seq_contrato_inter_w		bigint;
ds_erro_w			varchar(4000) := '';
nr_seq_tabela_w			bigint;


BEGIN 
 
select	ie_tabela_base, 
	coalesce(nr_seq_contrato_inter,0) 
into STRICT	ie_tabela_base_w, 
	nr_seq_contrato_inter_w 
from	pls_tabela_preco 
where	nr_sequencia = nr_seq_tabela_nova_p;
 
if (ie_tabela_base_w = 'S') then 
	CALL wheb_mensagem_pck.exibir_mensagem_abort(191855);
end if;
 
if (coalesce(nr_seq_interc_plano_p,0) <> 0) then	 
	ds_erro_w := pls_consiste_intercambio_plano(nr_seq_intercambio_p, nr_seq_plano_p, nr_seq_tabela_nova_p, nr_seq_interc_plano_p, ds_erro_w);
 
	if (coalesce(ds_erro_w,'X') <> 'X') then 
		CALL wheb_mensagem_pck.exibir_mensagem_abort(191805,'DS_ERRO='||ds_erro_w);
	end if;
	 
	if (coalesce(nr_seq_intercambio_p,0) <> nr_seq_contrato_inter_w) then 
		CALL pls_gerar_tabela_intercambio(nr_seq_intercambio_p, nr_seq_plano_p, nr_seq_tabela_nova_p, cd_estabelecimento_p, 'N', nm_usuario_p);
	else 
		update	pls_intercambio_plano 
		set	nr_seq_tabela	= nr_seq_tabela_nova_p 
		where	nr_sequencia	= nr_seq_interc_plano_p;
	end if;
 
end if;
 
commit;
 
end;
$body$
