SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_alterar_tabela_sca ( nr_seq_vinculo_sca_p bigint, nr_seq_tabela_p bigint, dt_alteracao_sca_p timestamp, ie_tipo_alteracao_p text, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE

 
/*	ie_tipo_alteracao_p 
	B - tabela do beneficiário 
	D - tabela do beneficiário e seus dependetes 
	T - todos os beneficiários com o produto selecionado 
	P - Todos os dependentes 
	O - Para os beneficiários da tabela de origem 
	R - Todos os beneficiários do produto selecionado 
*/
 
nr_seq_plano_sca_w		pls_sca_vinculo.nr_seq_plano%type;
nr_seq_contrato_w		pls_segurado.nr_seq_contrato%type;
nr_contrato_w			pls_contrato.nr_contrato%type;
nr_seq_segurado_w		pls_segurado.nr_sequencia%type;
ds_plano_w			pls_plano.ds_plano%type;
nr_seq_tabela_ant_w		pls_sca_vinculo.nr_seq_tabela%type;
nm_tabela_ant_w			pls_tabela_preco.nm_tabela%type;
nm_tabela_nova_w		pls_tabela_preco.nm_tabela%type;
ds_historico_w			varchar(4000);
nr_seq_vinculo_sca_w		pls_sca_vinculo.nr_sequencia%type;
nr_seq_tabela_origem_w		pls_sca_vinculo.nr_seq_tabela_origem%type;
ie_tipo_alteracao_w		varchar(1);
ie_tabela_contrato_w		varchar(1);
ie_tabela_base_w		varchar(1);
nr_seq_tabela_gerada_w		bigint;
ie_tipo_contratacao_w		varchar(3);
ds_erro_w			varchar(4000) := '';
nm_tabela_atual_w		varchar(255);
nr_seq_tabela_w			bigint;
nr_seq_tabela_atual_w		bigint;
dt_fim_vigencia_w		timestamp;
dt_contratacao_w		timestamp;
dt_alteracao_plano_w		timestamp;
dt_liberacao_w			timestamp;
nr_seq_tab_ant_contr_w		bigint;

c01 CURSOR FOR 
	SELECT	nr_seq_segurado, 
		nr_seq_vinculo_sca, 
		nr_seq_tabela_ant 
	from	(SELECT	a.nr_sequencia nr_seq_segurado, 
			b.nr_sequencia nr_seq_vinculo_sca, 
			b.nr_seq_tabela nr_seq_tabela_ant, 
			a.nr_seq_titular 
		from	pls_segurado a, 
			pls_sca_vinculo b 
		where	a.nr_sequencia		= b.nr_seq_segurado 
		and	b.nr_seq_plano		= nr_seq_plano_sca_w 
		and	((a.nr_sequencia	= nr_seq_segurado_w) 
		or (a.nr_seq_titular	= nr_seq_segurado_w)) 
		and (coalesce(a.dt_rescisao::text, '') = '' or a.dt_rescisao > clock_timestamp()) 
		and	(a.dt_liberacao IS NOT NULL AND a.dt_liberacao::text <> '') 
		and	ie_tipo_alteracao_w	= 'D' 
		
union all
 
		select	a.nr_sequencia nr_seq_segurado, 
			b.nr_sequencia nr_seq_vinculo_sca, 
			b.nr_seq_tabela nr_seq_tabela_ant, 
			a.nr_seq_titular 
		from	pls_segurado a, 
			pls_sca_vinculo b 
		where	a.nr_sequencia		= b.nr_seq_segurado 
		and	b.nr_seq_plano		= nr_seq_plano_sca_w 
		and	a.nr_seq_contrato	= nr_seq_contrato_w 
		and (coalesce(a.dt_rescisao::text, '') = '' or a.dt_rescisao > clock_timestamp()) 
		and	(a.dt_liberacao IS NOT NULL AND a.dt_liberacao::text <> '') 
		and	ie_tipo_alteracao_w	= 'T' 
		
union all
 
		select	a.nr_sequencia nr_seq_segurado, 
			b.nr_sequencia nr_seq_vinculo_sca, 
			b.nr_seq_tabela nr_seq_tabela_ant, 
			a.nr_seq_titular 
		from	pls_segurado a, 
			pls_sca_vinculo b 
		where	a.nr_sequencia		= b.nr_seq_segurado 
		and	b.nr_seq_plano		= nr_seq_plano_sca_w 
		and	a.nr_seq_contrato	= nr_seq_contrato_w 
		and (coalesce(a.dt_rescisao::text, '') = '' or a.dt_rescisao > clock_timestamp()) 
		and	(a.dt_liberacao IS NOT NULL AND a.dt_liberacao::text <> '') 
		and	ie_tipo_alteracao_w	= 'P' 
		and	(a.nr_seq_titular IS NOT NULL AND a.nr_seq_titular::text <> '') 
		
union all
 
		select a.nr_sequencia, 
			b.nr_sequencia nr_seq_vinculo_sca, 
			b.nr_seq_tabela nr_seq_tabela_ant, 
			a.nr_seq_titular 
		from 	pls_segurado a, 
			pls_sca_vinculo b 
		where 	a.nr_sequencia		= b.nr_seq_segurado 
		and	b.nr_seq_plano		= nr_seq_plano_sca_w 
		and	a.nr_seq_contrato 	= nr_seq_contrato_w 
		and (coalesce(a.dt_rescisao::text, '') = '' or a.dt_rescisao > clock_timestamp()) 
		and 	(a.dt_liberacao IS NOT NULL AND a.dt_liberacao::text <> '') 
		and 	ie_tipo_alteracao_w = 'O' 
		and 	b.nr_seq_tabela_origem = nr_seq_tabela_origem_w) alias20 
	order by	nr_seq_titular desc;


BEGIN 
ie_tipo_alteracao_w	:= coalesce(ie_tipo_alteracao_p,'X');
 
select	a.nr_seq_segurado, 
	a.nr_seq_plano, 
	a.nr_seq_tabela_origem, 
	b.ds_plano 
into STRICT	nr_seq_segurado_w, 
	nr_seq_plano_sca_w, 
	nr_seq_tabela_origem_w, 
	ds_plano_w 
from	pls_sca_vinculo	a, 
	pls_plano b 
where	b.nr_sequencia	= a.nr_seq_plano 
and	a.nr_sequencia	= nr_seq_vinculo_sca_p;
 
select	nm_tabela 
into STRICT	nm_tabela_nova_w 
from	pls_tabela_preco 
where	nr_sequencia	= nr_seq_tabela_p;
 
select	max(nr_seq_contrato) 
into STRICT	nr_seq_contrato_w 
from	pls_segurado 
where	nr_sequencia	= nr_seq_segurado_w;
 
if (ie_tipo_alteracao_w in ('B','D','T','P','O','R')) then 
	if (dt_fim_vigencia_w < clock_timestamp()) then 
		CALL wheb_mensagem_pck.exibir_mensagem_abort(191860);
	end if;
	 
	if (ie_tipo_alteracao_w = 'B') then 
		CALL pls_alterar_tabela_preco_sca(nr_seq_vinculo_sca_p,nr_seq_tabela_p,dt_alteracao_sca_p,cd_estabelecimento_p,nm_usuario_p);
	elsif (ie_tipo_alteracao_w in ('D','T','P','O')) then 
		open c01;
		loop 
		fetch c01 into 
			nr_seq_segurado_w, 
			nr_seq_vinculo_sca_w, 
			nr_seq_tabela_ant_w;
		EXIT WHEN NOT FOUND; /* apply on c01 */
			begin 
			select	nm_tabela 
			into STRICT	nm_tabela_ant_w 
			from	pls_tabela_preco 
			where	nr_sequencia	= nr_seq_tabela_ant_w;
			 
			ds_historico_w	:= 'Alterada a tabela de preço do SCA -> ' || ds_plano_w || ' de ' || nr_seq_tabela_ant_w || ' - ' || nm_tabela_ant_w || 
						' para ' || nr_seq_tabela_p || ' - ' ||nm_tabela_nova_w;
			 
			--Consisitir a nova tabela de preço do beneficiário 
			ds_erro_w := pls_consistir_plano_sca(nr_seq_segurado_w, nr_seq_plano_sca_w, nr_seq_tabela_p, null, cd_estabelecimento_p, ds_erro_w);
			 
			if (ds_erro_w IS NOT NULL AND ds_erro_w::text <> '') then 
				CALL wheb_mensagem_pck.exibir_mensagem_abort(191805,'DS_ERRO='||ds_erro_w);
			end if;
			 
			update	pls_sca_vinculo 
			set	nr_seq_tabela	= nr_seq_tabela_p, 
				dt_atualizacao	= clock_timestamp(), 
				nm_usuario	= nm_usuario_p 
			where	nr_sequencia	= nr_seq_vinculo_sca_w;
			 
			--Duplicar a tabela de preço 
			CALL pls_duplicar_tabela_preco_sca(nr_seq_contrato_w,nr_seq_vinculo_sca_w,nr_seq_tabela_p,'N',nm_usuario_p);
			 
			--Gerar o valor da tabela de preço 
			CALL pls_gerar_valor_tabela_sca(nr_seq_segurado_w,nr_seq_vinculo_sca_w,'A',dt_alteracao_sca_p,nm_usuario_p,cd_estabelecimento_p);
			 
			--Gerar o histórico de alteração 
			CALL pls_gerar_segurado_historico(	nr_seq_segurado_w, '36', clock_timestamp(), ds_historico_w, 
							'pls_alterar_tabela_preco_sca', null, null, null, 
							null, null, null, null, 
							null, null, null, null, 
							nm_usuario_p, 'N');					
			end;
		end loop;
		close c01;
	end if;
end if;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE pls_alterar_tabela_sca ( nr_seq_vinculo_sca_p bigint, nr_seq_tabela_p bigint, dt_alteracao_sca_p timestamp, ie_tipo_alteracao_p text, cd_estabelecimento_p bigint, nm_usuario_p text) FROM PUBLIC;

