SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_alterar_tipo_cat ( nr_seq_cat_p bigint, ie_tipo_cat_p text, nm_usuario_p text) AS $body$
DECLARE


dt_acidente_w			timestamp;
ie_local_acidente_w		varchar(2);
ds_situacao_geradora_w		varchar(4000);
nm_testemunha_w			varchar(4000);
nm_testemunha_ww		varchar(4000);
ds_data_acidente_w		varchar(255);
ds_local_acidente_w		varchar(255);
ds_log_w			varchar(4000);
ie_status_w			varchar(2)	:= 'A';
dt_reabertura_w			timestamp;

C01 CURSOR FOR
	SELECT	nm_testemunha
	from	pls_cat_testemunha
	where	nr_seq_cat	= nr_seq_cat_p
	order by nm_testemunha;


BEGIN

begin
	select	dt_acidente,
		ie_local_acidente,
		substr('Situação geradora: '||ds_situacao_geradora,1,4000)
	into STRICT	dt_acidente_w,
		ie_local_acidente_w,
		ds_situacao_geradora_w
	from	pls_cat_acidente_doenca
	where	nr_seq_cat	= nr_seq_cat_p;
exception
when others then
	dt_acidente_w		:= null;
	ie_local_acidente_w	:= null;
	ds_situacao_geradora_w	:= null;
end;

if (ie_tipo_cat_p	= '2') then
	dt_reabertura_w	:= clock_timestamp();
	ie_status_w	:= 'R';
end if;

if (dt_acidente_w IS NOT NULL AND dt_acidente_w::text <> '') then
	ds_data_acidente_w	:= substr('Data / hora acidente: '||to_char(dt_acidente_w,'dd/mm/yyyy hh24:mi:ss')||'.',1,255);
end if;

if (ie_local_acidente_w IS NOT NULL AND ie_local_acidente_w::text <> '') then
	ds_local_acidente_w	:= substr('Local do acidente: '||substr(obter_valor_dominio(2618,ie_local_acidente_w),1,255)||'.',1,255);
end if;

open C01;
loop
fetch C01 into
	nm_testemunha_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	if (coalesce(nm_testemunha_ww::text, '') = '') then
		nm_testemunha_ww	:= 'Testemunhas: '||chr(10);
	end if;

	nm_testemunha_ww	:= substr(nm_testemunha_ww||nm_testemunha_w||';'||chr(10),1,4000);
	end;
end loop;
close C01;

ds_log_w	:= ds_data_acidente_w||chr(10)||ds_local_acidente_w||chr(10)||ds_situacao_geradora_w||chr(10)||nm_testemunha_ww;

update	pls_cat
set	ie_tipo_cat	= ie_tipo_cat_p,
	dt_atualizacao	= clock_timestamp(),
	nm_usuario	= nm_usuario_p,
	ie_status	= ie_status_w,
	dt_reabertura	= dt_reabertura_w
where	nr_sequencia	= nr_seq_cat_p;

insert	into pls_cat_historico(ds_historico, dt_atualizacao, dt_atualizacao_nrec,
	 dt_historico, ie_tipo_historico, nm_usuario,
	 nm_usuario_nrec, nr_seq_cat, nr_sequencia)
values (substr('O usuário '||upper(nm_usuario_p)||' alterou o tipo da CAT para '||substr(obter_valor_dominio(2615,ie_tipo_cat_p),1,255)||' em '||
	 to_char(clock_timestamp(),'dd/mm/yyyy hh24:mi:ss')||'.'||chr(10)||ds_log_w,1,4000), clock_timestamp(), clock_timestamp(),
	 clock_timestamp(), 'L', nm_usuario_p,
	 nm_usuario_p, nr_seq_cat_p, nextval('pls_cat_historico_seq'));

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
