SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_alterar_tipo_conta_analise (nr_seq_analise_p bigint, nr_seq_tipo_conta_p bigint, nm_usuario_p text) AS $body$
DECLARE


/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade: Alterar o tipo de conta de todas as contas envolvidas na análise, para poder classificar as
mesmas de forma diferenciada na função OPS - Faturamento.
Rotina desenvolvida para ser utilizada inicialmente na Análise de Custo Pós
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[  ]  Objetos do dicionário [ X] Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção:
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
nr_seq_conta_w	bigint;

c01 CURSOR FOR
SELECT	a.nr_seq_conta
from	pls_conta_pos_estabelecido a
where	a.nr_seq_analise	= nr_seq_analise_p
and	((a.ie_situacao		= 'A') or (coalesce(a.ie_situacao::text, '') = ''))
group by
	a.nr_seq_conta;


BEGIN
if (nr_seq_analise_p IS NOT NULL AND nr_seq_analise_p::text <> '') then

	open c01;
	loop
	fetch c01 into
		nr_seq_conta_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
		begin

		update pls_conta_pos_cabecalho
		set	nr_seq_tipo_conta	= nr_seq_tipo_conta_p
		where	nr_seq_conta		= nr_seq_conta_w;

		update	pls_conta
		set	nr_seq_tipo_conta	= nr_seq_tipo_conta_p,
			nm_usuario		= nm_usuario_p,
			dt_atualizacao		= clock_timestamp()
		where	nr_sequencia		= nr_seq_conta_w;
		end;
	end loop;
	close c01;

	commit;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE pls_alterar_tipo_conta_analise (nr_seq_analise_p bigint, nr_seq_tipo_conta_p bigint, nm_usuario_p text) FROM PUBLIC;

