CREATE OR REPLACE PROCEDURE pls_alterar_tipo_documento_pf ( cd_pessoa_fisica_p text, nr_seq_tipo_documento_p bigint, nr_seq_documento_p bigint, nm_usuario_p text) AS $body$
DECLARE


qt_doc_pessoa_fisica_w		bigint;
nr_seq_tipo_doc_pf_w		bigint;
ds_arquivo_w			varchar(500);

BEGIN

select	ds_arquivo
into STRICT	ds_arquivo_w
from	pls_documento_pf
where	nr_sequencia	= nr_seq_documento_p;

select	max(nr_sequencia)
into STRICT	nr_seq_tipo_doc_pf_w
from	pls_tipo_documento_pf
where	nr_seq_tipo_documento	= nr_seq_tipo_documento_p
and	cd_pessoa_fisica	= cd_pessoa_fisica_p;

select	count(*)
into STRICT	qt_doc_pessoa_fisica_w
from	pls_documento_pf
where	nr_seq_tipo_doc_pf = nr_seq_tipo_doc_pf_w;

if (coalesce(nr_seq_tipo_doc_pf_w,0) = 0) then
	CALL wheb_mensagem_pck.exibir_mensagem_abort(265201,'');
	--Mensagem: Esse tipo de documento nÃ£o possui documentos cadastrados. Favor verificar!
else
	if (qt_doc_pessoa_fisica_w > 0) then
		update	pls_documento_pf
		set	nr_seq_tipo_doc_pf	= nr_seq_tipo_doc_pf_w
		where	nr_sequencia		= nr_seq_documento_p;
	else
		delete	FROM pls_documento_pf
		where	nr_sequencia = nr_seq_documento_p;

		insert into pls_documento_pf(	nr_sequencia,dt_atualizacao,nm_usuario,
				dt_atualizacao_nrec,nm_usuario_nrec,ds_arquivo,
				nr_seq_tipo_doc_pf	)
		values (	nextval('pls_documento_pf_seq'),clock_timestamp(),nm_usuario_p,
				clock_timestamp(),nm_usuario_p,ds_arquivo_w,
				nr_seq_tipo_doc_pf_w	);

	end if;
end if;
commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
