CREATE OR REPLACE PROCEDURE pls_alterar_tipo_envio_sib ( nr_seq_status_p pls_segurado_status.nr_sequencia%type, ie_envio_sib_p pls_segurado_status.ie_envio_sib%type, ie_exclusao_sib_p pls_segurado_status.ie_exclusao_sib%type, nm_usuario_p usuario.nm_usuario%type, cd_estabelecimento_p estabelecimento.cd_estabelecimento%type) AS $body$
DECLARE


nr_seq_segurado_w	pls_segurado_status.nr_seq_segurado%type;
ie_envio_sib_w		pls_segurado_status.ie_envio_sib%type;
ie_exclusao_sib_w	pls_segurado_status.ie_exclusao_sib%type;


BEGIN
if (nr_seq_status_p IS NOT NULL AND nr_seq_status_p::text <> '') then
	select	nr_seq_segurado,
		ie_envio_sib,
		ie_exclusao_sib
	into STRICT	nr_seq_segurado_w,
		ie_envio_sib_w,
		ie_exclusao_sib_w
	from 	pls_segurado_status
	where 	nr_sequencia = nr_seq_status_p
	order by 1 desc LIMIT 1;

	if (nr_seq_status_p IS NOT NULL AND nr_seq_status_p::text <> '') then
		if (coalesce(ie_envio_sib_p,0) <> coalesce(ie_envio_sib_w,0)) then
			update	pls_segurado_status
			set 	ie_envio_sib	= ie_envio_sib_p,
				dt_atualizacao	= clock_timestamp(),
				nm_usuario	= nm_usuario_p
			where	nr_sequencia 	= nr_seq_status_p;

			/*Gravar histórico no beneficiário*/

			CALL pls_gerar_segurado_historico(	nr_seq_segurado_w, '94', clock_timestamp(), 'Alterado o tipo de envio SIB.',
							'pls_alterar_tipo_envio_sib', null, null, null,
							null, clock_timestamp(), null, null,
							null, null, null, null,
							nm_usuario_p, 'N');
		end if;

		if (coalesce(ie_exclusao_sib_p,0) <> coalesce(ie_exclusao_sib_w,0)) then
			update	pls_segurado_status
			set 	ie_exclusao_sib	= ie_exclusao_sib_p,
				dt_atualizacao	= clock_timestamp(),
				nm_usuario	= nm_usuario_p
			where	nr_sequencia 	= nr_seq_status_p;

			/*Gravar histórico no beneficiário*/

			CALL pls_gerar_segurado_historico(	nr_seq_segurado_w, '94', clock_timestamp(), 'Alterado o tipo de Exclusão SIB.',
							'pls_alterar_tipo_envio_sib', null, null, null,
							null, clock_timestamp(), null, null,
							null, null, null, null,
							nm_usuario_p, 'N');
		end if;

		commit;
	end if;
end if;

end;
$body$
