CREATE OR REPLACE PROCEDURE pls_alterar_tipo_interno_prop ( nr_seq_proposta_p pls_proposta_adesao.nr_sequencia%type, nr_seq_tipo_interno_p pls_status_interno_prop.nr_sequencia%type, nm_usuario_p usuario.nm_usuario%type) AS $body$
DECLARE


nr_seq_status_interno_w	bigint;
ds_historico_w		varchar(255);


BEGIN

select	max(nr_seq_status_interno)
into STRICT	nr_seq_status_interno_w
from	pls_proposta_adesao
where	nr_sequencia	= nr_seq_proposta_p;

update	pls_proposta_adesao
set	nr_seq_status_interno	= nr_seq_tipo_interno_p,
	nm_usuario		= nm_usuario_p,
	dt_atualizacao		= clock_timestamp()
where	nr_sequencia		= nr_seq_proposta_p;

ds_historico_w	:= 'O Status interno foi alterado de "'||pls_desc_status_interno_prop(nr_seq_status_interno_w)||'" para "'|| pls_desc_status_interno_prop(nr_seq_tipo_interno_p)||'"';

insert	into	pls_proposta_historico(	nr_sequencia, nr_seq_proposta, dt_historico,
		ds_historico, dt_atualizacao, nm_usuario,
		dt_atualizacao_nrec, nm_usuario_nrec, ie_origem_historico,
		dt_liberacao_historico, nm_usuario_liberacao)
	values (	nextval('pls_proposta_historico_seq'), nr_seq_proposta_p, clock_timestamp(),
		ds_historico_w, clock_timestamp(), nm_usuario_p,
		clock_timestamp(), nm_usuario_p, 1,
		clock_timestamp(), nm_usuario_p);

commit;

end;
