SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_alterar_titular_benef_prop ( nr_seq_benef_p bigint, nr_seq_titular_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint) AS $body$
DECLARE

 
nr_seq_titular_w		bigint;
nm_titular_w			varchar(255);
nr_seq_proposta_w		bigint;
nm_beneficiario_w		varchar(255);
nm_titular_novo_w		varchar(255);


BEGIN 
 
if (coalesce(nr_seq_benef_p,0) <> 0) then 
	select	nr_seq_titular, 
		substr(pls_obter_dados_benef_prop(nr_seq_titular,'N'),1,255), 
		nr_seq_proposta, 
		substr(obter_nome_pf(cd_beneficiario),1,255) 
	into STRICT	nr_seq_titular_w, 
		nm_titular_w, 
		nr_seq_proposta_w, 
		nm_beneficiario_w 
	from	pls_proposta_beneficiario 
	where	nr_sequencia	= nr_seq_benef_p;
	 
	if (coalesce(nr_seq_titular_w,0)	<> coalesce(nr_seq_titular_p,0)) then 
		update	pls_proposta_beneficiario 
		set	nr_seq_titular	= CASE WHEN nr_seq_titular_p=0 THEN null  ELSE nr_seq_titular_p END  
		where	nr_sequencia	= nr_seq_benef_p;
		 
		select	substr(pls_obter_dados_benef_prop(nr_seq_titular_p,'N'),1,255) 
		into STRICT	nm_titular_novo_w 
		;
		 
		insert into pls_proposta_historico(	nr_sequencia, 
							nr_seq_proposta, 
							dt_historico, 
							ds_historico, 
							dt_atualizacao, 
							nm_usuario, 
							dt_atualizacao_nrec, 
							nm_usuario_nrec, 
							ie_origem_historico) 
						values (	nextval('pls_proposta_historico_seq'), 
							nr_seq_proposta_w, 
							clock_timestamp(), 
							'Alterada a titularidade do benefici√°rio ' || nm_beneficiario_w || ' de ' || nm_titular_w || ' para ' || nm_titular_novo_w || '.', 
							clock_timestamp(), 
							nm_usuario_p, 
							clock_timestamp(), 
							nm_usuario_p, 
							1);
		 
		CALL pls_gerar_simulacao_proposta(nr_seq_proposta_w, nm_usuario_p, cd_estabelecimento_p);
	end if;
	 
end if;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
