CREATE OR REPLACE PROCEDURE pls_alterar_tp_parentesco ( nr_seq_segurado_p bigint, ie_tipo_parentesco_p text, nm_usuario_p text) AS $body$
DECLARE


ie_tipo_parentesco_antec_w	varchar(3);
cd_estabelecimento_w		pls_segurado.cd_estabelecimento%type;


BEGIN

if (ie_tipo_parentesco_p IS NOT NULL AND ie_tipo_parentesco_p::text <> '') then
	select	ie_tipo_parentesco,
		cd_estabelecimento
	into STRICT	ie_tipo_parentesco_antec_w,
		cd_estabelecimento_w
	from	pls_segurado
	where	nr_sequencia = nr_seq_segurado_p;
	
	if (coalesce(ie_tipo_parentesco_antec_w,'X') <> ie_tipo_parentesco_p) then
		update	pls_segurado
		set	ie_tipo_parentesco	= ie_tipo_parentesco_p,
			nm_usuario		= nm_usuario_p
		where	nr_sequencia		= nr_seq_segurado_p;
		
		CALL pls_preco_beneficiario_pck.gravar_preco_benef(nr_seq_segurado_p, 'T', 'S', clock_timestamp(), 'N', null, nm_usuario_p, cd_estabelecimento_w);
		
		CALL pls_preco_beneficiario_pck.atualizar_preco_beneficiarios(nr_seq_segurado_p, null, null, null, clock_timestamp(), null, 'N', nm_usuario_p, cd_estabelecimento_w);
				
		CALL pls_gerar_segurado_historico(	nr_seq_segurado_p, '69', clock_timestamp(), 'De: ' || obter_valor_dominio(1885, ie_tipo_parentesco_antec_w)  || ' Para: ' || obter_valor_dominio(1885, ie_tipo_parentesco_p),
						'pls_alterar_tp_parentesco', null, null, null,
						null, clock_timestamp(), null, null,
						null, null, null, null,
						nm_usuario_p, 'N');				
	end if;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
