CREATE OR REPLACE PROCEDURE pls_alterar_tp_paren_inter ( nr_seq_segurado_p bigint, ie_tipo_parentesco_p text, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE

				 
ie_tipo_parentesco_ant_w	varchar(10);


BEGIN 
 
if (ie_tipo_parentesco_p IS NOT NULL AND ie_tipo_parentesco_p::text <> '') then 
 
	select	ie_tipo_parentesco 
	into STRICT	ie_tipo_parentesco_ant_w 
	from	pls_segurado 
	where	nr_sequencia		= nr_seq_segurado_p;
 
	update	pls_segurado 
	set	ie_tipo_parentesco	= ie_tipo_parentesco_p, 
		nm_usuario		= nm_usuario_p 
	where	nr_sequencia		= nr_seq_segurado_p;
	 
	CALL pls_recalcular_preco_benef(nr_seq_segurado_p,'R','S',clock_timestamp(),'N',nm_usuario_p,cd_estabelecimento_p);
	 
	CALL pls_gerar_segurado_historico(	nr_seq_segurado_p, '65', clock_timestamp(), 'Alteração do tipo parentesco de: '|| obter_valor_dominio(1885,ie_tipo_parentesco_ant_w)||' para: '||obter_valor_dominio(1885,ie_tipo_parentesco_p), 
					'pls_alterar_tp_paren_inter', null, null, null, 
					null, null, null, null, 
					null, null, null, null, 
					nm_usuario_p, 'N');
end if;
 
commit;
 
end;
