CREATE OR REPLACE PROCEDURE pls_alterar_utilizacao_incons ( nr_seq_inconsistencia_p bigint, ie_utilizacao_p text, nm_usuario_p text) AS $body$
DECLARE


cd_inconsistencia_w	pls_inconsistencia_inc_seg.cd_inconsistencia%type;


BEGIN

select	cd_inconsistencia
into STRICT	cd_inconsistencia_w
from	pls_inconsistencia_inc_seg
where	nr_sequencia = nr_seq_inconsistencia_p;

if (ie_utilizacao_p in ('A','T')) then -- A = ambos / T = Tasy
	if (cd_inconsistencia_w = 1) then
		CALL wheb_mensagem_pck.exibir_mensagem_abort(358961, null);
		-- Mensagem: Esta inconsistência é respeitada pelo Tasy quando o parâmetro [106] - Permite a geração de contratos sem beneficiários vinculados - estiver recebendo 'N'.
	elsif (cd_inconsistencia_w in (2,4,5,6,7,19,20,23,26,29,32,34,36,38,40,41,42,45,47,48)) then
		CALL wheb_mensagem_pck.exibir_mensagem_abort(358964, null);
		-- Mensagem: Inconsistência não tratada para o Tasy. Se necessário favor abrir uma nova OS.
	end if;
end if;

update	pls_inconsistencia_inc_seg
set	ie_utilizacao 	= ie_utilizacao_p,
	nm_usuario 	= nm_usuario_p,
	dt_atualizacao 	= clock_timestamp()
where	nr_sequencia = nr_seq_inconsistencia_p;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
