CREATE OR REPLACE PROCEDURE pls_alterar_venc_escrit_quota ( nr_seq_escrituracao_p pls_escrituracao_quota.nr_sequencia%type, nr_seq_parcela_p pls_escrit_quota_parcela.nr_sequencia%type, dt_vencimento_p timestamp, nm_usuario_p usuario.nm_usuario%type) AS $body$
DECLARE


dt_vencimento_w	 	timestamp;
dt_sysdate_w		timestamp;


BEGIN

dt_vencimento_w := trunc(dt_vencimento_p,'dd');
dt_sysdate_w 	:= trunc(clock_timestamp(),'dd');

if (dt_vencimento_w < dt_sysdate_w ) then
	CALL wheb_mensagem_pck.exibir_mensagem_abort(328563);
	--A data deve ser maior ou igual a data atual do sistema.
end if;

update	pls_escrit_quota_parcela
set	dt_vencimento			= dt_vencimento_p
where	nr_parcela			= nr_seq_parcela_p
and	nr_seq_escrituracao		= nr_seq_escrituracao_p;

commit;


end;
$body$
