CREATE OR REPLACE PROCEDURE pls_alterar_via_carteira ( nr_seq_segurado_p bigint, nr_via_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint) AS $body$
DECLARE


nr_seq_carteira_w		bigint;



BEGIN

select	nr_sequencia
into STRICT	nr_seq_carteira_w
from	pls_segurado_carteira
where	nr_seq_segurado = nr_seq_segurado_p;

update	pls_segurado_carteira
set	nr_via_solicitacao 	= nr_via_p,
	dt_atualizacao		= clock_timestamp(),
	nm_usuario		= nm_usuario_p
where	nr_sequencia		= nr_seq_carteira_w;

/*Gravar histórico no beneficiário*/

CALL pls_gerar_segurado_historico(	nr_seq_segurado_p, '70', clock_timestamp(), 'Alterado o número da via da carteira.',
				'pls_alterar_via_carteira', null, null, null,
				null, clock_timestamp(), null, null,
				null, null, null, null,
				nm_usuario_p, 'N');
commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
