CREATE OR REPLACE PROCEDURE pls_alterar_via_devolucao ( nr_seq_cart_devolucao_p bigint, nr_via_p bigint, nr_via_atual_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_estagio_devolucao_w	pls_segurado_cart_estagio.nr_sequencia%type;


BEGIN

if (nr_seq_cart_devolucao_p IS NOT NULL AND nr_seq_cart_devolucao_p::text <> '') and (coalesce(nr_via_p,0) <> coalesce(nr_via_atual_p,0)) then
	select	max(nr_sequencia)
	into STRICT	nr_seq_estagio_devolucao_w
	from	pls_segurado_cart_estagio
	where	nr_seq_devolucao	= nr_seq_cart_devolucao_p;

	update	pls_segurado_cart_estagio
	set	nr_via	= nr_via_p
	where	nr_sequencia	= nr_seq_estagio_devolucao_w;

	update	pls_carteira_devolucao
	set	nr_via	= nr_via_p
	where	nr_sequencia	= nr_seq_cart_devolucao_p;

	commit;
end if;

end;
