CREATE OR REPLACE PROCEDURE pls_alterar_vinculo_estip ( nr_seq_segurado_p bigint, nr_seq_vinculo_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint) AS $body$
DECLARE


nr_seq_vinculo_est_w		bigint;
ds_vinculo_w			varchar(255);
ds_vinculo_novo_w		varchar(255);


BEGIN

select	coalesce(max(nr_seq_vinculo_est),0)
into STRICT	nr_seq_vinculo_est_w
from	pls_segurado
where	nr_sequencia	= nr_seq_segurado_p;

if (nr_seq_vinculo_est_w <> 0) then
	select	ds_vinculo
	into STRICT	ds_vinculo_w
	from	pls_vinculo_estipulante
	where	nr_sequencia	= nr_seq_vinculo_est_w;
end if;

select	max(ds_vinculo)
into STRICT	ds_vinculo_novo_w
from	pls_vinculo_estipulante
where	nr_sequencia	= nr_seq_vinculo_p;

update 	pls_segurado
set 	nr_seq_vinculo_est	= nr_seq_vinculo_p
where	nr_sequencia 		= nr_seq_segurado_p;

/* Gerar histórico */

CALL pls_gerar_segurado_historico(
	nr_seq_segurado_p, '18', clock_timestamp(),
	'Alteração do vínculo com o estipulante de ' || ds_vinculo_w || ' para ' || ds_vinculo_novo_w, 'pls_alterar_vinculo_estip',
	null,
	null, null, null,
	clock_timestamp(), null, null,
	null, null, null,
	null, nm_usuario_p, 'S');

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
