CREATE OR REPLACE PROCEDURE pls_alterar_vinculo_operadora ( nr_seq_segurado_p bigint, ie_vinculo_operadora_p text, nm_usuario_p text) AS $body$
DECLARE


ie_vinculo_operadora_w	varchar(10);


BEGIN

select	ie_tipo_vinculo_operadora
into STRICT	ie_vinculo_operadora_w
from	pls_segurado
where	nr_sequencia			= nr_seq_segurado_p;

update	pls_segurado
set	ie_tipo_vinculo_operadora	= ie_vinculo_operadora_p,
	nm_usuario			= nm_usuario_p,
	dt_atualizacao			= clock_timestamp()
where	nr_sequencia			= nr_seq_segurado_p;

CALL pls_gerar_segurado_historico(	nr_seq_segurado_p, '34', clock_timestamp(), 'Alteração do tipo de vínculo da operadora de '|| substr(obter_valor_dominio(3652,ie_vinculo_operadora_w),1,50) ||
				' para '|| substr(obter_valor_dominio(3652,ie_vinculo_operadora_p),1,50),
				'pls_alterar_vinculo_operadora', null, null, null,
				null, null, null, null,
				null, null, null, null,
				nm_usuario_p, 'N');

commit;

end;
$body$
