CREATE OR REPLACE PROCEDURE pls_alterar_vl_nao_subsidiado ( nr_seq_tabela_p bigint, vl_nao_subsidiado_p text, nm_usuario_p text, cd_complemento_p bigint) AS $body$
DECLARE


nr_seq_fx_etaria_w	bigint;

C01 CURSOR FOR
	SELECT	nr_sequencia
	from	pls_plano_preco
	where	nr_seq_tabela = nr_seq_tabela_p;


BEGIN

if (vl_nao_subsidiado_p >= 0) then

	open C01;
	loop
	fetch C01 into
		nr_seq_fx_etaria_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin

		update	pls_plano_preco
		set	vl_preco_nao_subsidiado = vl_nao_subsidiado_p,
			nm_usuario = nm_usuario_p,
			dt_atualizacao = clock_timestamp()
		where	nr_sequencia = nr_seq_fx_etaria_w;

		end;
	end loop;
	close C01;

end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
