SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_altera_bonif_cooperado_seg ( nr_seq_segurado_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_titular_w	bigint;
qt_dependente_w		bigint;
nr_seq_dependente_w	bigint;
nm_pessoa_dependente_w	varchar(255);
ie_bonific_cooperado_w	varchar(1);
ds_historico_w		pls_segurado_historico.ds_historico%type;


BEGIN

select	max(nr_seq_titular),
	max(ie_bonific_cooperado)
into STRICT	nr_seq_titular_w,
	ie_bonific_cooperado_w
from	pls_segurado
where	nr_sequencia	= nr_seq_segurado_p;

if (coalesce(ie_bonific_cooperado_w,'N') = 'N') then
	select	count(*)
	into STRICT	qt_dependente_w
	from	pls_segurado
	where	nr_seq_titular	= nr_seq_titular_w
	and	nr_sequencia	<> nr_seq_segurado_p
	and	ie_bonific_cooperado	= 'S';

	if (qt_dependente_w <> 0) then
		select	max(a.nr_sequencia),
			max(b.nm_pessoa_fisica)
		into STRICT	nr_seq_dependente_w,
			nm_pessoa_dependente_w
		from	pls_segurado	a,
			pessoa_fisica	b
		where	a.cd_pessoa_fisica	= b.cd_pessoa_fisica
		and	a.nr_seq_titular	= nr_seq_titular_w
		and	a.nr_sequencia		<> nr_seq_segurado_p
		and	ie_bonific_cooperado	= 'S';

		CALL wheb_mensagem_pck.exibir_mensagem_abort(233340, 'NR_SEQ_DEPENDENTE=' || nr_seq_dependente_w || ';NM_PESSOA_DEPENDENTE=' || nm_pessoa_dependente_w);
		/* Mensagem: Não é possível habilitar a bonificação pois o beneficiário #@NR_SEQ_DEPENDENTE#@ - #@NM_PESSOA_DEPENDENTE#@ está habilitado para receber a mesma! */

	else
		update	pls_segurado
		set	ie_bonific_cooperado	= 'S'
		where	nr_sequencia	= nr_seq_segurado_p;
	end if;
else
	update	pls_segurado
	set	ie_bonific_cooperado	= 'N'
	where	nr_sequencia	= nr_seq_segurado_p;
end if;

select	'De: ' || CASE WHEN coalesce(ie_bonific_cooperado_w,'N')='N' THEN 'Não'  ELSE 'Sim' END   || ' Para: ' || CASE WHEN coalesce(ie_bonific_cooperado_w,'N')='S' THEN 'Não'  ELSE 'Sim' END
into STRICT	ds_historico_w
;

CALL pls_gerar_segurado_historico(	nr_seq_segurado_p, '68', clock_timestamp(), ds_historico_w,
				'pls_altera_bonif_cooperado_seg', null, null, null,
				null, null, null, null,
				null, null, null, null,
				nm_usuario_p, 'N');

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE pls_altera_bonif_cooperado_seg ( nr_seq_segurado_p bigint, nm_usuario_p text) FROM PUBLIC;

