CREATE OR REPLACE PROCEDURE pls_altera_evento_excessao ( nr_seq_combinada_p pls_oc_cta_combinada.nr_sequencia%type, nr_seq_ocorrencia_p pls_oc_cta_combinada.nr_seq_ocorrencia%type, nm_usuario_p text) AS $body$
DECLARE


ie_evento_w				pls_oc_cta_combinada.ie_evento%type;
ie_portal_web_w				pls_oc_cta_combinada.ie_portal_web%type;

C01 CURSOR(	nr_seq_combinada_pc	pls_oc_cta_combinada.nr_sequencia%type) FOR
	SELECT	nr_sequencia
	from	pls_oc_cta_combinada
	where	nr_seq_combinada = nr_seq_combinada_pc;

BEGIN

if (nr_seq_combinada_p IS NOT NULL AND nr_seq_combinada_p::text <> '' AND nr_seq_ocorrencia_p IS NOT NULL AND nr_seq_ocorrencia_p::text <> '') then

	select	ie_evento,
		ie_portal_web
	into STRICT	ie_evento_w,
		ie_portal_web_w
	from 	pls_oc_cta_combinada
	where	nr_sequencia = nr_seq_combinada_p;

	for r_C01_w in C01(nr_seq_combinada_p) loop
		begin
		update 	pls_oc_cta_combinada
		set	nm_usuario 		= nm_usuario_p,
			dt_atualizacao 		= clock_timestamp(),
			ie_portal_web		= ie_portal_web_w,
			ie_evento		= ie_evento_w
		where	nr_sequencia 		= r_C01_w.nr_sequencia;
		end;
	end loop;
	commit;
end if;

end;
$body$
